import '@picocss/pico'
import { Metadata } from 'next'
import { GoogleAnalytics } from 'src/components/GoogleAnalytics'
import { Layout } from 'src/components/Layout'
import { ImageModalProvider } from 'src/lib/context/ImageModalContext'
import { ImageModal } from 'src/components/ImageModal'

export const metadata: Metadata = {
  title: {
    default: "dOwOd's logs",
    template: "%s | dOwOd's logs",
  },
}

export default function RootLayout({
  children,
}: {
  children: React.ReactNode
}) {
  return (
    <html lang="ja">
      <body>
        <ImageModalProvider>
          <Layout>
            {children}
          </Layout>
          <ImageModal />
        </ImageModalProvider>
        {process.env.NEXT_PUBLIC_GA4_ID && (
          <GoogleAnalytics gaId={process.env.NEXT_PUBLIC_GA4_ID} />
        )}
      </body>
    </html>
  )
}

import { Metadata } from 'next'
import { initializeApollo } from 'src/lib/apolloClient'
import { GET_ARTICLE_BY_SLUG, GET_ARTICLES_BY_STATUS } from 'src/queries/queries'
import { GetArticleBySlugQuery, GetArticlesByStatusQuery } from 'src/gql/graphql'
import { Article } from 'src/components/Article'
import { notFound } from 'next/navigation'

type Props = {
  params: Promise<{
    slug: string
  }>
}

export const generateStaticParams = async () => {
  const { data } = await initializeApollo().query<GetArticlesByStatusQuery>({
    query: GET_ARTICLES_BY_STATUS,
    variables: { status: 'public' },
  })
  if (!data) return []
  return data.articles.map((article) => ({ slug: article.slug }))
}

export async function generateMetadata(props: Props): Promise<Metadata> {
  const params = await props.params
  const { data } = await initializeApollo().query<GetArticleBySlugQuery>({
    query: GET_ARTICLE_BY_SLUG,
    variables: { slug: params.slug },
  })

  const article = data?.articles[0]
  if (!article) {
    return { title: 'Not Found' }
  }

  return {
    title: article.title,
  }
}

const Page = async (props: Props) => {
  const params = await props.params;
  const { data } = await initializeApollo().query<GetArticleBySlugQuery>({
    query: GET_ARTICLE_BY_SLUG,
    variables: { slug: params.slug },
  })

  const article = data?.articles[0]
  if (!article) return notFound()


  const blogPostingJsonLd = {
    '@context': 'https://schema.org',
    '@type': 'BlogPosting',
    headline: article.title,
    datePublished: article.created_at,
    dateModified: article.updated_at,
    author: { '@type': 'Person', name: 'dOwOd', url: 'https://dowo.dev' },
    url: `https://dowo.dev/blog/${params.slug}`,
  }

  const breadcrumbJsonLd = {
    '@context': 'https://schema.org',
    '@type': 'BreadcrumbList',
    itemListElement: [
      {
        '@type': 'ListItem',
        position: 1,
        name: 'Home',
        item: 'https://dowo.dev',
      },
      {
        '@type': 'ListItem',
        position: 2,
        name: article.title,
        item: `https://dowo.dev/blog/${params.slug}`,
      },
    ],
  }

  return (
    <>
      <script
        type="application/ld+json"
        dangerouslySetInnerHTML={{ __html: JSON.stringify(blogPostingJsonLd) }}
      />
      <script
        type="application/ld+json"
        dangerouslySetInnerHTML={{ __html: JSON.stringify(breadcrumbJsonLd) }}
      />
      <Article article={article} />
    </>
  )
}

export default Page

